<script>
import { TweenMax, TimelineMax, Power4 } from 'gsap';
export default {
  name: 'SaveTheDate',
  mounted () {
    this.playing()
  },
  methods: {
    playing () {
      let yHeight = document.documentElement.clientHeight
      //std
      TweenMax.set('#std', { 
        scale: 4,
        y: yHeight/2 - 100,
        transformOrigin: 'center'
      })
      let tl = new TimelineMax();
      tl
        .to('#std', 1.5, { scaleX: 4, ease: Power4.easeOut, delay: 3 })
        .to('#std', 0.3, { y: yHeight/2 - 80, ease: Power4.inOut })
        .to('#std', 1.5, { scale:1, y: 0, ease: Power4.inOut, onComplete: () => { 
          this.onCompleteHandle()
          TweenMax.fromTo( '#std',1,
            {
              visible:true,
              scale:1
            },
            {
              scale:1.2,
              repeat: -1,
              yoyo: true
            })
        } })
    },
    onCompleteHandle () {
      this.$emit('onCompleteStep1Handle')
    }
  }
}
</script>

